package day1;

public class WritingNames10Times {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int m = 1; m <= 10; m++) {
			System.out.println("Sayantan");
		}

		int m = 1;
		while (m <= 10) {
			System.out.println("Bepari");
			m++;
		}

	}

}
